function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(k){var m=$("#active-addresses");var a=m.find("table");a.find("tbody").remove();for(var d in k){var l=k[d];if(d==0){a.append('<tbody><tr><th colspan="2">Group #'+d+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{a.append('<tbody><tr><th colspan="4">Group #'+d+"</th></tr></tbody>")}var n=$("<tbody></tbody>").appendTo(a);for(var c in l){var j=l[c];var h=addresses[j];if(!h){continue}var g="";if(h.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var f="";var b=h.addr;if(h.label!=null){b=h.label;f='<span class="hidden-phone"> - '+h.addr+"</span>"}var e='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+b+"</a>"+f+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';n.append(e)}a.append(n)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(c){var k=[];var e=c.filteredTaints;for(var j in e){var m=e[j];var l=-1;for(var h in m){var n=m[h];for(var g in k){if(g!=l&&$.inArray(h,k[g])){if(l>=0){var d=k.splice(l,1);var f=k.splice(g,1);k.push(d.concat(f))}else{k[g].push(j)}l=g}if(l>=0){break}}}if(l==-1){k.push([j])}}buildTable(k);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(k)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};