function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(a){var c=$("#active-addresses");var b=c.find("table");b.find("tbody").remove();for(var k in a){var m=a[k];if(k==0){b.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{b.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var n=$("<tbody></tbody>").appendTo(b);for(var h in m){var l=m[h];var j=addresses[l];if(!j){continue}var g="";if(j.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(j.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var f="";var e=j.addr;if(j.label!=null){e=j.label;f='<span class="hidden-phone"> - '+j.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+j.addr+'" onclick="showAddressModal(\''+j.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+j.addr+'" target="new">'+e+"</a>"+f+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(j.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';n.append(d)}b.append(n)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(k){var c=[];var f=k.filteredTaints;for(var n in f){var g=f[n];var e=-1;for(var h in g){var l=g[h];for(var m in c){if(m!=e&&$.inArray(h,c[m])){if(e>=0){var d=c.splice(e,1);var j=c.splice(m,1);c.push(d.concat(j))}else{c[m].push(n)}e=m}if(e>=0){break}}}if(e==-1){c.push([n])}}buildTable(c);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};