function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var l=$("#active-addresses");var f=l.find("table");f.find("tbody").remove();for(var n in g){var e=g[n];if(n==0){f.append('<tbody><tr><th colspan="2">Group #'+n+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{f.append('<tbody><tr><th colspan="4">Group #'+n+"</th></tr></tbody>")}var b=$("<tbody></tbody>").appendTo(f);for(var j in e){var d=e[j];var k=addresses[d];if(!k){continue}var a="";if(k.tag==1){a=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){a=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var m="";var c=k.addr;if(k.label!=null){c=k.label;m='<span class="hidden-phone"> - '+k.addr+"</span>"}var h='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+c+"</a>"+m+" "+a+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';b.append(h)}f.append(b)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(c){var j=[];var h=c.filteredTaints;for(var f in h){var k=h[f];var m=-1;for(var d in k){var l=k[d];for(var n in j){if(n!=m&&$.inArray(d,j[n])){if(m>=0){var g=j.splice(m,1);var e=j.splice(n,1);j.push(g.concat(e))}else{j[n].push(f)}m=n}if(m>=0){break}}}if(m==-1){j.push([f])}}buildTable(j);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};