function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var c=$("#active-addresses");var k=c.find("table");k.find("tbody").remove();for(var g in n){var f=n[g];if(g==0){k.append('<tbody><tr><th colspan="2">Group #'+g+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{k.append('<tbody><tr><th colspan="4">Group #'+g+"</th></tr></tbody>")}var m=$("<tbody></tbody>").appendTo(k);for(var e in f){var d=f[e];var j=addresses[d];if(!j){continue}var l="";if(j.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(j.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var a=j.addr;if(j.label!=null){a=j.label;b='<span class="hidden-phone"> - '+j.addr+"</span>"}var h='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+j.addr+'" onclick="showAddressModal(\''+j.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+j.addr+'" target="new">'+a+"</a>"+b+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(j.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';m.append(h)}k.append(m)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(c){var m=[];var g=c.filteredTaints;for(var d in g){var j=g[d];var f=-1;for(var n in j){var l=j[n];for(var e in m){if(e!=f&&$.inArray(n,m[e])){if(f>=0){var h=m.splice(f,1);var k=m.splice(e,1);m.push(h.concat(k))}else{m[e].push(d)}f=e}if(f>=0){break}}}if(f==-1){m.push([d])}}buildTable(m);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(m)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};