function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(m){var n=$("#active-addresses");var b=n.find("table");b.find("tbody").remove();for(var h in m){var j=m[h];if(h==0){b.append('<tbody><tr><th colspan="2">Group #'+h+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{b.append('<tbody><tr><th colspan="4">Group #'+h+"</th></tr></tbody>")}var l=$("<tbody></tbody>").appendTo(b);for(var d in j){var k=j[d];var g=addresses[k];if(!g){continue}var a="";if(g.tag==1){a=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(g.priv==null){a=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var e=g.addr;if(g.label!=null){e=g.label;c='<span class="hidden-phone"> - '+g.addr+"</span>"}var f='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+g.addr+'" onclick="showAddressModal(\''+g.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+g.addr+'" target="new">'+e+"</a>"+c+" "+a+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(g.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';l.append(f)}b.append(l)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(f){var n=[];var g=f.filteredTaints;for(var l in g){var j=g[l];var e=-1;for(var d in j){var m=j[d];for(var h in n){if(h!=e&&$.inArray(d,n[h])){if(e>=0){var k=n.splice(e,1);var c=n.splice(h,1);n.push(k.concat(c))}else{n[h].push(l)}e=h}if(e>=0){break}}}if(e==-1){n.push([l])}}buildTable(n);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};