function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(c){var a=$("#active-addresses");var e=a.find("table");e.find("tbody").remove();for(var l in c){var d=c[l];if(l==0){e.append('<tbody><tr><th colspan="2">Group #'+l+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{e.append('<tbody><tr><th colspan="4">Group #'+l+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(e);for(var n in d){var f=d[n];var b=addresses[f];if(!b){continue}var h="";if(b.tag==1){h=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(b.priv==null){h=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var g="";var m=b.addr;if(b.label!=null){m=b.label;g='<span class="hidden-phone"> - '+b.addr+"</span>"}var j='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+b.addr+'" onclick="showAddressModal(\''+b.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+b.addr+'" target="new">'+m+"</a>"+g+" "+h+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(b.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(j)}e.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(f){var c=[];var e=f.filteredTaints;for(var d in e){var h=e[d];var m=-1;for(var n in h){var g=h[n];for(var k in c){if(k!=m&&$.inArray(n,c[k])){if(m>=0){var l=c.splice(m,1);var j=c.splice(k,1);c.push(l.concat(j))}else{c[k].push(d)}m=k}if(m>=0){break}}}if(m==-1){c.push([d])}}buildTable(c);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};