function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(j){var n=$("#active-addresses");var h=n.find("table");h.find("tbody").remove();for(var a in j){var f=j[a];if(a==0){h.append('<tbody><tr><th colspan="2">Group #'+a+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+a+"</th></tr></tbody>")}var g=$("<tbody></tbody>").appendTo(h);for(var c in f){var m=f[c];var l=addresses[m];if(!l){continue}var b="";if(l.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var d="";var e=l.addr;if(l.label!=null){e=l.label;d='<span class="hidden-phone"> - '+l.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+e+"</a>"+d+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';g.append(k)}h.append(g)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var h=[];var j=n.filteredTaints;for(var m in j){var d=j[m];var e=-1;for(var f in d){var g=d[f];for(var c in h){if(c!=e&&$.inArray(f,h[c])){if(e>=0){var k=h.splice(e,1);var l=h.splice(c,1);h.push(k.concat(l))}else{h[c].push(m)}e=c}if(e>=0){break}}}if(e==-1){h.push([m])}}buildTable(h);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(h)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};