function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(h){var m=$("#active-addresses");var l=m.find("table");l.find("tbody").remove();for(var k in h){var g=h[k];if(k==0){l.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var a=$("<tbody></tbody>").appendTo(l);for(var f in g){var n=g[f];var j=addresses[n];if(!j){continue}var d="";if(j.tag==1){d=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(j.priv==null){d=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var e="";var b=j.addr;if(j.label!=null){b=j.label;e='<span class="hidden-phone"> - '+j.addr+"</span>"}var c='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+j.addr+'" onclick="showAddressModal(\''+j.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+j.addr+'" target="new">'+b+"</a>"+e+" "+d+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(j.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';a.append(c)}l.append(a)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(e){var h=[];var k=e.filteredTaints;for(var l in k){var g=k[l];var n=-1;for(var f in g){var d=g[f];for(var j in h){if(j!=n&&$.inArray(f,h[j])){if(n>=0){var m=h.splice(n,1);var c=h.splice(j,1);h.push(m.concat(c))}else{h[j].push(l)}n=j}if(n>=0){break}}}if(n==-1){h.push([l])}}buildTable(h);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(h)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};