function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(b){var n=$("#active-addresses");var k=n.find("table");k.find("tbody").remove();for(var a in b){var g=b[a];if(a==0){k.append('<tbody><tr><th colspan="2">Group #'+a+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{k.append('<tbody><tr><th colspan="4">Group #'+a+"</th></tr></tbody>")}var f=$("<tbody></tbody>").appendTo(k);for(var m in g){var e=g[m];var d=addresses[e];if(!d){continue}var c="";if(d.tag==1){c=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){c=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var l="";var j=d.addr;if(d.label!=null){j=d.label;l='<span class="hidden-phone"> - '+d.addr+"</span>"}var h='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+j+"</a>"+l+" "+c+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';f.append(h)}k.append(f)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var e=[];var h=l.filteredTaints;for(var g in h){var n=h[g];var f=-1;for(var j in n){var m=n[j];for(var c in e){if(c!=f&&$.inArray(j,e[c])){if(f>=0){var d=e.splice(f,1);var k=e.splice(c,1);e.push(d.concat(k))}else{e[c].push(g)}f=c}if(f>=0){break}}}if(f==-1){e.push([g])}}buildTable(e);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};