function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(l){var k=$("#active-addresses");var e=k.find("table");e.find("tbody").remove();for(var m in l){var f=l[m];if(m==0){e.append('<tbody><tr><th colspan="2">Group #'+m+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{e.append('<tbody><tr><th colspan="4">Group #'+m+"</th></tr></tbody>")}var b=$("<tbody></tbody>").appendTo(e);for(var n in f){var g=f[n];var h=addresses[g];if(!h){continue}var j="";if(h.tag==1){j=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){j=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var a="";var d=h.addr;if(h.label!=null){d=h.label;a='<span class="hidden-phone"> - '+h.addr+"</span>"}var c='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+d+"</a>"+a+" "+j+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';b.append(c)}e.append(b)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(j){var k=[];var h=j.filteredTaints;for(var g in h){var c=h[g];var l=-1;for(var f in c){var d=c[f];for(var n in k){if(n!=l&&$.inArray(f,k[n])){if(l>=0){var e=k.splice(l,1);var m=k.splice(n,1);k.push(e.concat(m))}else{k[n].push(g)}l=n}if(l>=0){break}}}if(l==-1){k.push([g])}}buildTable(k);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(k)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};