function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var k=$("#active-addresses");var c=k.find("table");c.find("tbody").remove();for(var g in n){var m=n[g];if(g==0){c.append('<tbody><tr><th colspan="2">Group #'+g+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{c.append('<tbody><tr><th colspan="4">Group #'+g+"</th></tr></tbody>")}var d=$("<tbody></tbody>").appendTo(c);for(var h in m){var j=m[h];var l=addresses[j];if(!l){continue}var e="";if(l.tag==1){e=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){e=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var a="";var b=l.addr;if(l.label!=null){b=l.label;a='<span class="hidden-phone"> - '+l.addr+"</span>"}var f='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+b+"</a>"+a+" "+e+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';d.append(f)}c.append(d)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(k){var n=[];var e=k.filteredTaints;for(var j in e){var c=e[j];var l=-1;for(var h in c){var d=c[h];for(var g in n){if(g!=l&&$.inArray(h,n[g])){if(l>=0){var f=n.splice(l,1);var m=n.splice(g,1);n.push(f.concat(m))}else{n[g].push(j)}l=g}if(l>=0){break}}}if(l==-1){n.push([j])}}buildTable(n);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};