function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(h){var j=$("#active-addresses");var f=j.find("table");f.find("tbody").remove();for(var n in h){var e=h[n];if(n==0){f.append('<tbody><tr><th colspan="2">Group #'+n+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{f.append('<tbody><tr><th colspan="4">Group #'+n+"</th></tr></tbody>")}var d=$("<tbody></tbody>").appendTo(f);for(var l in e){var b=e[l];var k=addresses[b];if(!k){continue}var m="";if(k.tag==1){m=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){m=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var a="";var g=k.addr;if(k.label!=null){g=k.label;a='<span class="hidden-phone"> - '+k.addr+"</span>"}var c='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+g+"</a>"+a+" "+m+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';d.append(c)}f.append(d)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(d){var j=[];var l=d.filteredTaints;for(var e in l){var k=l[e];var m=-1;for(var g in k){var f=k[g];for(var n in j){if(n!=m&&$.inArray(g,j[n])){if(m>=0){var h=j.splice(m,1);var c=j.splice(n,1);j.push(h.concat(c))}else{j[n].push(e)}m=n}if(m>=0){break}}}if(m==-1){j.push([e])}}buildTable(j);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};