function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(d){var m=$("#active-addresses");var n=m.find("table");n.find("tbody").remove();for(var h in d){var b=d[h];if(h==0){n.append('<tbody><tr><th colspan="2">Group #'+h+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{n.append('<tbody><tr><th colspan="4">Group #'+h+"</th></tr></tbody>")}var a=$("<tbody></tbody>").appendTo(n);for(var k in b){var j=b[k];var l=addresses[j];if(!l){continue}var e="";if(l.tag==1){e=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){e=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var g="";var f=l.addr;if(l.label!=null){f=l.label;g='<span class="hidden-phone"> - '+l.addr+"</span>"}var c='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+f+"</a>"+g+" "+e+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';a.append(c)}n.append(a)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(e){var g=[];var n=e.filteredTaints;for(var m in n){var k=n[m];var h=-1;for(var f in k){var d=k[f];for(var l in g){if(l!=h&&$.inArray(f,g[l])){if(h>=0){var c=g.splice(h,1);var j=g.splice(l,1);g.push(c.concat(j))}else{g[l].push(m)}h=l}if(h>=0){break}}}if(h==-1){g.push([m])}}buildTable(g);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(g)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};