function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var m=$("#active-addresses");var l=m.find("table");l.find("tbody").remove();for(var b in g){var k=g[b];if(b==0){l.append('<tbody><tr><th colspan="2">Group #'+b+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+b+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(l);for(var j in k){var c=k[j];var e=addresses[c];if(!e){continue}var f="";if(e.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(e.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var d="";var n=e.addr;if(e.label!=null){n=e.label;d='<span class="hidden-phone"> - '+e.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+e.addr+'" onclick="showAddressModal(\''+e.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+e.addr+'" target="new">'+n+"</a>"+d+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(e.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(a)}l.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(k){var h=[];var l=k.filteredTaints;for(var f in l){var d=l[f];var m=-1;for(var g in d){var j=d[g];for(var e in h){if(e!=m&&$.inArray(g,h[e])){if(m>=0){var n=h.splice(m,1);var c=h.splice(e,1);h.push(n.concat(c))}else{h[e].push(f)}m=e}if(m>=0){break}}}if(m==-1){h.push([f])}}buildTable(h);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(h)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};