function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(d){var m=$("#active-addresses");var l=m.find("table");l.find("tbody").remove();for(var e in d){var f=d[e];if(e==0){l.append('<tbody><tr><th colspan="2">Group #'+e+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+e+"</th></tr></tbody>")}var n=$("<tbody></tbody>").appendTo(l);for(var c in f){var h=f[c];var a=addresses[h];if(!a){continue}var g="";if(a.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(a.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var j="";var k=a.addr;if(a.label!=null){k=a.label;j='<span class="hidden-phone"> - '+a.addr+"</span>"}var b='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+a.addr+'" onclick="showAddressModal(\''+a.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+a.addr+'" target="new">'+k+"</a>"+j+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(a.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';n.append(b)}l.append(n)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(m){var j=[];var f=m.filteredTaints;for(var n in f){var l=f[n];var e=-1;for(var c in l){var g=l[c];for(var h in j){if(h!=e&&$.inArray(c,j[h])){if(e>=0){var k=j.splice(e,1);var d=j.splice(h,1);j.push(k.concat(d))}else{j[h].push(n)}e=h}if(e>=0){break}}}if(e==-1){j.push([n])}}buildTable(j);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};