function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(d){var c=$("#active-addresses");var e=c.find("table");e.find("tbody").remove();for(var k in d){var n=d[k];if(k==0){e.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{e.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var f=$("<tbody></tbody>").appendTo(e);for(var b in n){var l=n[b];var h=addresses[l];if(!h){continue}var j="";if(h.tag==1){j=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){j=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var a="";var m=h.addr;if(h.label!=null){m=h.label;a='<span class="hidden-phone"> - '+h.addr+"</span>"}var g='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+m+"</a>"+a+" "+j+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';f.append(g)}e.append(f)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(j){var f=[];var l=j.filteredTaints;for(var n in l){var c=l[n];var e=-1;for(var d in c){var k=c[d];for(var m in f){if(m!=e&&$.inArray(d,f[m])){if(e>=0){var h=f.splice(e,1);var g=f.splice(m,1);f.push(h.concat(g))}else{f[m].push(n)}e=m}if(e>=0){break}}}if(e==-1){f.push([n])}}buildTable(f);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};