function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var c=$("#active-addresses");var f=c.find("table");f.find("tbody").remove();for(var h in n){var l=n[h];if(h==0){f.append('<tbody><tr><th colspan="2">Group #'+h+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{f.append('<tbody><tr><th colspan="4">Group #'+h+"</th></tr></tbody>")}var e=$("<tbody></tbody>").appendTo(f);for(var a in l){var d=l[a];var k=addresses[d];if(!k){continue}var b="";if(k.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var m="";var j=k.addr;if(k.label!=null){j=k.label;m='<span class="hidden-phone"> - '+k.addr+"</span>"}var g='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+j+"</a>"+m+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';e.append(g)}f.append(e)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(d){var m=[];var l=d.filteredTaints;for(var e in l){var f=l[e];var n=-1;for(var g in f){var h=f[g];for(var j in m){if(j!=n&&$.inArray(g,m[j])){if(n>=0){var k=m.splice(n,1);var c=m.splice(j,1);m.push(k.concat(c))}else{m[j].push(e)}n=j}if(n>=0){break}}}if(n==-1){m.push([e])}}buildTable(m);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(m)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};