function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(a){var f=$("#active-addresses");var j=f.find("table");j.find("tbody").remove();for(var h in a){var b=a[h];if(h==0){j.append('<tbody><tr><th colspan="2">Group #'+h+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{j.append('<tbody><tr><th colspan="4">Group #'+h+"</th></tr></tbody>")}var e=$("<tbody></tbody>").appendTo(j);for(var n in b){var c=b[n];var l=addresses[c];if(!l){continue}var g="";if(l.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var d=l.addr;if(l.label!=null){d=l.label;k='<span class="hidden-phone"> - '+l.addr+"</span>"}var m='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+d+"</a>"+k+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';e.append(m)}j.append(e)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(f){var g=[];var h=f.filteredTaints;for(var k in h){var j=h[k];var e=-1;for(var n in j){var d=j[n];for(var l in g){if(l!=e&&$.inArray(n,g[l])){if(e>=0){var m=g.splice(e,1);var c=g.splice(l,1);g.push(m.concat(c))}else{g[l].push(k)}e=l}if(e>=0){break}}}if(e==-1){g.push([k])}}buildTable(g);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(g)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};