function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(c){var g=$("#active-addresses");var n=g.find("table");n.find("tbody").remove();for(var b in c){var d=c[b];if(b==0){n.append('<tbody><tr><th colspan="2">Group #'+b+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{n.append('<tbody><tr><th colspan="4">Group #'+b+"</th></tr></tbody>")}var l=$("<tbody></tbody>").appendTo(n);for(var m in d){var j=d[m];var k=addresses[j];if(!k){continue}var h="";if(k.tag==1){h=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){h=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var f="";var a=k.addr;if(k.label!=null){a=k.label;f='<span class="hidden-phone"> - '+k.addr+"</span>"}var e='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+a+"</a>"+f+" "+h+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';l.append(e)}n.append(l)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(c){var e=[];var g=c.filteredTaints;for(var j in g){var h=g[j];var n=-1;for(var m in h){var k=h[m];for(var d in e){if(d!=n&&$.inArray(m,e[d])){if(n>=0){var f=e.splice(n,1);var l=e.splice(d,1);e.push(f.concat(l))}else{e[d].push(j)}n=d}if(n>=0){break}}}if(n==-1){e.push([j])}}buildTable(e);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};