function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var j=$("#active-addresses");var k=j.find("table");k.find("tbody").remove();for(var l in e){var b=e[l];if(l==0){k.append('<tbody><tr><th colspan="2">Group #'+l+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{k.append('<tbody><tr><th colspan="4">Group #'+l+"</th></tr></tbody>")}var f=$("<tbody></tbody>").appendTo(k);for(var n in b){var g=b[n];var h=addresses[g];if(!h){continue}var d="";if(h.tag==1){d=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){d=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var m=h.addr;if(h.label!=null){m=h.label;c='<span class="hidden-phone"> - '+h.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+m+"</a>"+c+" "+d+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';f.append(a)}k.append(f)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(g){var c=[];var k=g.filteredTaints;for(var d in k){var h=k[d];var m=-1;for(var e in h){var n=h[e];for(var j in c){if(j!=m&&$.inArray(e,c[j])){if(m>=0){var f=c.splice(m,1);var l=c.splice(j,1);c.push(f.concat(l))}else{c[j].push(d)}m=j}if(m>=0){break}}}if(m==-1){c.push([d])}}buildTable(c);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};