function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(h){var b=$("#active-addresses");var n=b.find("table");n.find("tbody").remove();for(var j in h){var a=h[j];if(j==0){n.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{n.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var f=$("<tbody></tbody>").appendTo(n);for(var k in a){var d=a[k];var e=addresses[d];if(!e){continue}var c="";if(e.tag==1){c=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(e.priv==null){c=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var m="";var l=e.addr;if(e.label!=null){l=e.label;m='<span class="hidden-phone"> - '+e.addr+"</span>"}var g='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+e.addr+'" onclick="showAddressModal(\''+e.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+e.addr+'" target="new">'+l+"</a>"+m+" "+c+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(e.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';f.append(g)}n.append(f)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(j){var l=[];var n=j.filteredTaints;for(var g in n){var d=n[g];var e=-1;for(var f in d){var h=d[f];for(var k in l){if(k!=e&&$.inArray(f,l[k])){if(e>=0){var c=l.splice(e,1);var m=l.splice(k,1);l.push(c.concat(m))}else{l[k].push(g)}e=k}if(e>=0){break}}}if(e==-1){l.push([g])}}buildTable(l);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(l)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};