function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(f){var j=$("#active-addresses");var e=j.find("table");e.find("tbody").remove();for(var c in f){var l=f[c];if(c==0){e.append('<tbody><tr><th colspan="2">Group #'+c+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{e.append('<tbody><tr><th colspan="4">Group #'+c+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(e);for(var g in l){var h=l[g];var b=addresses[h];if(!b){continue}var a="";if(b.tag==1){a=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(b.priv==null){a=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var n="";var m=b.addr;if(b.label!=null){m=b.label;n='<span class="hidden-phone"> - '+b.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+b.addr+'" onclick="showAddressModal(\''+b.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+b.addr+'" target="new">'+m+"</a>"+n+" "+a+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(b.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(d)}e.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(e){var f=[];var l=e.filteredTaints;for(var h in l){var j=l[h];var g=-1;for(var d in j){var m=j[d];for(var c in f){if(c!=g&&$.inArray(d,f[c])){if(g>=0){var n=f.splice(g,1);var k=f.splice(c,1);f.push(n.concat(k))}else{f[c].push(h)}g=c}if(g>=0){break}}}if(g==-1){f.push([h])}}buildTable(f);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};