function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var j=$("#active-addresses");var l=j.find("table");l.find("tbody").remove();for(var f in n){var d=n[f];if(f==0){l.append('<tbody><tr><th colspan="2">Group #'+f+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+f+"</th></tr></tbody>")}var a=$("<tbody></tbody>").appendTo(l);for(var g in d){var e=d[g];var h=addresses[e];if(!h){continue}var k="";if(h.tag==1){k=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){k=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var m="";var b=h.addr;if(h.label!=null){b=h.label;m='<span class="hidden-phone"> - '+h.addr+"</span>"}var c='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+b+"</a>"+m+" "+k+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';a.append(c)}l.append(a)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(e){var n=[];var f=e.filteredTaints;for(var k in f){var c=f[k];var h=-1;for(var g in c){var j=c[g];for(var l in n){if(l!=h&&$.inArray(g,n[l])){if(h>=0){var d=n.splice(h,1);var m=n.splice(l,1);n.push(d.concat(m))}else{n[l].push(k)}h=l}if(h>=0){break}}}if(h==-1){n.push([k])}}buildTable(n);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};