function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var c=$("#active-addresses");var m=c.find("table");m.find("tbody").remove();for(var b in g){var f=g[b];if(b==0){m.append('<tbody><tr><th colspan="2">Group #'+b+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{m.append('<tbody><tr><th colspan="4">Group #'+b+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(m);for(var h in f){var j=f[h];var e=addresses[j];if(!e){continue}var n="";if(e.tag==1){n=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(e.priv==null){n=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var d="";var a=e.addr;if(e.label!=null){a=e.label;d='<span class="hidden-phone"> - '+e.addr+"</span>"}var l='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+e.addr+'" onclick="showAddressModal(\''+e.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+e.addr+'" target="new">'+a+"</a>"+d+" "+n+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(e.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(l)}m.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(k){var f=[];var d=k.filteredTaints;for(var j in d){var g=d[j];var m=-1;for(var e in g){var h=g[e];for(var c in f){if(c!=m&&$.inArray(e,f[c])){if(m>=0){var l=f.splice(m,1);var n=f.splice(c,1);f.push(l.concat(n))}else{f[c].push(j)}m=c}if(m>=0){break}}}if(m==-1){f.push([j])}}buildTable(f);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};