function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(a){var f=$("#active-addresses");var g=f.find("table");g.find("tbody").remove();for(var h in a){var j=a[h];if(h==0){g.append('<tbody><tr><th colspan="2">Group #'+h+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{g.append('<tbody><tr><th colspan="4">Group #'+h+"</th></tr></tbody>")}var d=$("<tbody></tbody>").appendTo(g);for(var e in j){var l=j[e];var k=addresses[l];if(!k){continue}var b="";if(k.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var c="";var m=k.addr;if(k.label!=null){m=k.label;c='<span class="hidden-phone"> - '+k.addr+"</span>"}var n='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+m+"</a>"+c+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';d.append(n)}g.append(d)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(m){var d=[];var e=m.filteredTaints;for(var j in e){var k=e[j];var g=-1;for(var c in k){var n=k[c];for(var f in d){if(f!=g&&$.inArray(c,d[f])){if(g>=0){var h=d.splice(g,1);var l=d.splice(f,1);d.push(h.concat(l))}else{d[f].push(j)}g=f}if(g>=0){break}}}if(g==-1){d.push([j])}}buildTable(d);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};