function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(f){var d=$("#active-addresses");var n=d.find("table");n.find("tbody").remove();for(var g in f){var a=f[g];if(g==0){n.append('<tbody><tr><th colspan="2">Group #'+g+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{n.append('<tbody><tr><th colspan="4">Group #'+g+"</th></tr></tbody>")}var m=$("<tbody></tbody>").appendTo(n);for(var c in a){var l=a[c];var j=addresses[l];if(!j){continue}var e="";if(j.tag==1){e=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(j.priv==null){e=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var b=j.addr;if(j.label!=null){b=j.label;k='<span class="hidden-phone"> - '+j.addr+"</span>"}var h='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+j.addr+'" onclick="showAddressModal(\''+j.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+j.addr+'" target="new">'+b+"</a>"+k+" "+e+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(j.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';m.append(h)}n.append(m)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(h){var g=[];var e=h.filteredTaints;for(var m in e){var c=e[m];var f=-1;for(var n in c){var d=c[n];for(var l in g){if(l!=f&&$.inArray(n,g[l])){if(f>=0){var k=g.splice(f,1);var j=g.splice(l,1);g.push(k.concat(j))}else{g[l].push(m)}f=l}if(f>=0){break}}}if(f==-1){g.push([m])}}buildTable(g);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(g)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};