function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(j){var g=$("#active-addresses");var e=g.find("table");e.find("tbody").remove();for(var h in j){var m=j[h];if(h==0){e.append('<tbody><tr><th colspan="2">Group #'+h+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{e.append('<tbody><tr><th colspan="4">Group #'+h+"</th></tr></tbody>")}var f=$("<tbody></tbody>").appendTo(e);for(var l in m){var c=m[l];var d=addresses[c];if(!d){continue}var b="";if(d.tag==1){b=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){b=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var n=d.addr;if(d.label!=null){n=d.label;k='<span class="hidden-phone"> - '+d.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+n+"</a>"+k+" "+b+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';f.append(a)}e.append(f)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var n=[];var c=l.filteredTaints;for(var g in c){var e=c[g];var k=-1;for(var f in e){var d=e[f];for(var m in n){if(m!=k&&$.inArray(f,n[m])){if(k>=0){var j=n.splice(k,1);var h=n.splice(m,1);n.push(j.concat(h))}else{n[m].push(g)}k=m}if(k>=0){break}}}if(k==-1){n.push([g])}}buildTable(n);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};