function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(b){var k=$("#active-addresses");var l=k.find("table");l.find("tbody").remove();for(var j in b){var e=b[j];if(j==0){l.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{l.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(l);for(var m in e){var f=e[m];var c=addresses[f];if(!c){continue}var n="";if(c.tag==1){n=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(c.priv==null){n=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var d="";var g=c.addr;if(c.label!=null){g=c.label;d='<span class="hidden-phone"> - '+c.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+c.addr+'" onclick="showAddressModal(\''+c.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+c.addr+'" target="new">'+g+"</a>"+d+" "+n+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(c.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(a)}l.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(c){var d=[];var n=c.filteredTaints;for(var h in n){var f=n[h];var l=-1;for(var g in f){var e=f[g];for(var k in d){if(k!=l&&$.inArray(g,d[k])){if(l>=0){var m=d.splice(l,1);var j=d.splice(k,1);d.push(m.concat(j))}else{d[k].push(h)}l=k}if(l>=0){break}}}if(l==-1){d.push([h])}}buildTable(d);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};