function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(c){var a=$("#active-addresses");var b=a.find("table");b.find("tbody").remove();for(var h in c){var k=c[h];if(h==0){b.append('<tbody><tr><th colspan="2">Group #'+h+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{b.append('<tbody><tr><th colspan="4">Group #'+h+"</th></tr></tbody>")}var g=$("<tbody></tbody>").appendTo(b);for(var n in k){var f=k[n];var l=addresses[f];if(!l){continue}var e="";if(l.tag==1){e=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){e=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var d="";var j=l.addr;if(l.label!=null){j=l.label;d='<span class="hidden-phone"> - '+l.addr+"</span>"}var m='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+j+"</a>"+d+" "+e+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';g.append(m)}b.append(g)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var c=[];var e=l.filteredTaints;for(var g in e){var m=e[g];var n=-1;for(var j in m){var f=m[j];for(var h in c){if(h!=n&&$.inArray(j,c[h])){if(n>=0){var k=c.splice(n,1);var d=c.splice(h,1);c.push(k.concat(d))}else{c[h].push(g)}n=h}if(n>=0){break}}}if(n==-1){c.push([g])}}buildTable(c);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};