function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var b=$("#active-addresses");var a=b.find("table");a.find("tbody").remove();for(var c in g){var h=g[c];if(c==0){a.append('<tbody><tr><th colspan="2">Group #'+c+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{a.append('<tbody><tr><th colspan="4">Group #'+c+"</th></tr></tbody>")}var f=$("<tbody></tbody>").appendTo(a);for(var k in h){var j=h[k];var n=addresses[j];if(!n){continue}var l="";if(n.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(n.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var e="";var m=n.addr;if(n.label!=null){m=n.label;e='<span class="hidden-phone"> - '+n.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+n.addr+'" onclick="showAddressModal(\''+n.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+n.addr+'" target="new">'+m+"</a>"+e+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(n.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';f.append(d)}a.append(f)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(d){var g=[];var e=d.filteredTaints;for(var h in e){var n=e[h];var m=-1;for(var l in n){var f=n[l];for(var c in g){if(c!=m&&$.inArray(l,g[c])){if(m>=0){var k=g.splice(m,1);var j=g.splice(c,1);g.push(k.concat(j))}else{g[c].push(h)}m=c}if(m>=0){break}}}if(m==-1){g.push([h])}}buildTable(g);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(g)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};