function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(k){var l=$("#active-addresses");var d=l.find("table");d.find("tbody").remove();for(var g in k){var c=k[g];if(g==0){d.append('<tbody><tr><th colspan="2">Group #'+g+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{d.append('<tbody><tr><th colspan="4">Group #'+g+"</th></tr></tbody>")}var m=$("<tbody></tbody>").appendTo(d);for(var n in c){var f=c[n];var b=addresses[f];if(!b){continue}var e="";if(b.tag==1){e=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(b.priv==null){e=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var j="";var a=b.addr;if(b.label!=null){a=b.label;j='<span class="hidden-phone"> - '+b.addr+"</span>"}var h='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+b.addr+'" onclick="showAddressModal(\''+b.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+b.addr+'" target="new">'+a+"</a>"+j+" "+e+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(b.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';m.append(h)}d.append(m)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var m=[];var d=l.filteredTaints;for(var f in d){var n=d[f];var g=-1;for(var j in n){var c=n[j];for(var k in m){if(k!=g&&$.inArray(j,m[k])){if(g>=0){var h=m.splice(g,1);var e=m.splice(k,1);m.push(h.concat(e))}else{m[k].push(f)}g=k}if(g>=0){break}}}if(g==-1){m.push([f])}}buildTable(m);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(m)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};