function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(d){var b=$("#active-addresses");var f=b.find("table");f.find("tbody").remove();for(var j in d){var g=d[j];if(j==0){f.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{f.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var n=$("<tbody></tbody>").appendTo(f);for(var e in g){var h=g[e];var a=addresses[h];if(!a){continue}var m="";if(a.tag==1){m=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(a.priv==null){m=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var l="";var c=a.addr;if(a.label!=null){c=a.label;l='<span class="hidden-phone"> - '+a.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+a.addr+'" onclick="showAddressModal(\''+a.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+a.addr+'" target="new">'+c+"</a>"+l+" "+m+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(a.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';n.append(k)}f.append(n)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(f){var c=[];var l=f.filteredTaints;for(var h in l){var e=l[h];var j=-1;for(var m in e){var d=e[m];for(var g in c){if(g!=j&&$.inArray(m,c[g])){if(j>=0){var n=c.splice(j,1);var k=c.splice(g,1);c.push(n.concat(k))}else{c[g].push(h)}j=g}if(j>=0){break}}}if(j==-1){c.push([h])}}buildTable(c);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};