function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(b){var m=$("#active-addresses");var f=m.find("table");f.find("tbody").remove();for(var d in b){var h=b[d];if(d==0){f.append('<tbody><tr><th colspan="2">Group #'+d+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{f.append('<tbody><tr><th colspan="4">Group #'+d+"</th></tr></tbody>")}var a=$("<tbody></tbody>").appendTo(f);for(var k in h){var j=h[k];var c=addresses[j];if(!c){continue}var l="";if(c.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(c.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var g="";var e=c.addr;if(c.label!=null){e=c.label;g='<span class="hidden-phone"> - '+c.addr+"</span>"}var n='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+c.addr+'" onclick="showAddressModal(\''+c.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+c.addr+'" target="new">'+e+"</a>"+g+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(c.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';a.append(n)}f.append(a)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(g){var d=[];var c=g.filteredTaints;for(var j in c){var m=c[j];var n=-1;for(var h in m){var l=m[h];for(var e in d){if(e!=n&&$.inArray(h,d[e])){if(n>=0){var k=d.splice(n,1);var f=d.splice(e,1);d.push(k.concat(f))}else{d[e].push(j)}n=e}if(n>=0){break}}}if(n==-1){d.push([j])}}buildTable(d);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};