function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var m=$("#active-addresses");var g=m.find("table");g.find("tbody").remove();for(var j in e){var b=e[j];if(j==0){g.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{g.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(g);for(var f in b){var l=b[f];var a=addresses[l];if(!a){continue}var n="";if(a.tag==1){n=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(a.priv==null){n=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var h="";var d=a.addr;if(a.label!=null){d=a.label;h='<span class="hidden-phone"> - '+a.addr+"</span>"}var c='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+a.addr+'" onclick="showAddressModal(\''+a.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+a.addr+'" target="new">'+d+"</a>"+h+" "+n+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(a.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(c)}g.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(e){var g=[];var f=e.filteredTaints;for(var k in f){var c=f[k];var n=-1;for(var m in c){var l=c[m];for(var h in g){if(h!=n&&$.inArray(m,g[h])){if(n>=0){var j=g.splice(n,1);var d=g.splice(h,1);g.push(j.concat(d))}else{g[h].push(k)}n=h}if(n>=0){break}}}if(n==-1){g.push([k])}}buildTable(g);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(g)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};