function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(g){var h=$("#active-addresses");var m=h.find("table");m.find("tbody").remove();for(var e in g){var j=g[e];if(e==0){m.append('<tbody><tr><th colspan="2">Group #'+e+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{m.append('<tbody><tr><th colspan="4">Group #'+e+"</th></tr></tbody>")}var c=$("<tbody></tbody>").appendTo(m);for(var b in j){var k=j[b];var l=addresses[k];if(!l){continue}var f="";if(l.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var d="";var n=l.addr;if(l.label!=null){n=l.label;d='<span class="hidden-phone"> - '+l.addr+"</span>"}var a='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+n+"</a>"+d+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';c.append(a)}m.append(c)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var k=[];var f=l.filteredTaints;for(var n in f){var j=f[n];var c=-1;for(var e in j){var g=j[e];for(var h in k){if(h!=c&&$.inArray(e,k[h])){if(c>=0){var m=k.splice(c,1);var d=k.splice(h,1);k.push(m.concat(d))}else{k[h].push(n)}c=h}if(c>=0){break}}}if(c==-1){k.push([n])}}buildTable(k);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(k)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};