function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(l){var m=$("#active-addresses");var h=m.find("table");h.find("tbody").remove();for(var d in l){var n=l[d];if(d==0){h.append('<tbody><tr><th colspan="2">Group #'+d+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+d+"</th></tr></tbody>")}var a=$("<tbody></tbody>").appendTo(h);for(var c in n){var g=n[c];var f=addresses[g];if(!f){continue}var k="";if(f.tag==1){k=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(f.priv==null){k=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var e=f.addr;if(f.label!=null){e=f.label;b='<span class="hidden-phone"> - '+f.addr+"</span>"}var j='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+f.addr+'" onclick="showAddressModal(\''+f.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+f.addr+'" target="new">'+e+"</a>"+b+" "+k+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(f.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';a.append(j)}h.append(a)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(d){var k=[];var h=d.filteredTaints;for(var e in h){var l=h[e];var g=-1;for(var j in l){var n=l[j];for(var c in k){if(c!=g&&$.inArray(j,k[c])){if(g>=0){var f=k.splice(g,1);var m=k.splice(c,1);k.push(f.concat(m))}else{k[c].push(e)}g=c}if(g>=0){break}}}if(g==-1){k.push([e])}}buildTable(k);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(k)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};