function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var f=$("#active-addresses");var k=f.find("table");k.find("tbody").remove();for(var b in n){var j=n[b];if(b==0){k.append('<tbody><tr><th colspan="2">Group #'+b+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{k.append('<tbody><tr><th colspan="4">Group #'+b+"</th></tr></tbody>")}var g=$("<tbody></tbody>").appendTo(k);for(var e in j){var m=j[e];var d=addresses[m];if(!d){continue}var a="";if(d.tag==1){a=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(d.priv==null){a=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var l="";var c=d.addr;if(d.label!=null){c=d.label;l='<span class="hidden-phone"> - '+d.addr+"</span>"}var h='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+d.addr+'" onclick="showAddressModal(\''+d.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+d.addr+'" target="new">'+c+"</a>"+l+" "+a+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(d.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';g.append(h)}k.append(g)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(f){var n=[];var g=f.filteredTaints;for(var k in g){var m=g[k];var c=-1;for(var l in m){var e=m[l];for(var d in n){if(d!=c&&$.inArray(l,n[d])){if(c>=0){var j=n.splice(c,1);var h=n.splice(d,1);n.push(j.concat(h))}else{n[d].push(k)}c=d}if(c>=0){break}}}if(c==-1){n.push([k])}}buildTable(n);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};