function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(b){var h=$("#active-addresses");var n=h.find("table");n.find("tbody").remove();for(var e in b){var f=b[e];if(e==0){n.append('<tbody><tr><th colspan="2">Group #'+e+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{n.append('<tbody><tr><th colspan="4">Group #'+e+"</th></tr></tbody>")}var l=$("<tbody></tbody>").appendTo(n);for(var m in f){var j=f[m];var c=addresses[j];if(!c){continue}var g="";if(c.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(c.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var a="";var k=c.addr;if(c.label!=null){k=c.label;a='<span class="hidden-phone"> - '+c.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+c.addr+'" onclick="showAddressModal(\''+c.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+c.addr+'" target="new">'+k+"</a>"+a+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(c.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';l.append(d)}n.append(l)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(m){var e=[];var g=m.filteredTaints;for(var h in g){var c=g[h];var d=-1;for(var n in c){var l=c[n];for(var f in e){if(f!=d&&$.inArray(n,e[f])){if(d>=0){var j=e.splice(d,1);var k=e.splice(f,1);e.push(j.concat(k))}else{e[f].push(h)}d=f}if(d>=0){break}}}if(d==-1){e.push([h])}}buildTable(e);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};