function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var h=$("#active-addresses");var m=h.find("table");m.find("tbody").remove();for(var k in e){var b=e[k];if(k==0){m.append('<tbody><tr><th colspan="2">Group #'+k+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{m.append('<tbody><tr><th colspan="4">Group #'+k+"</th></tr></tbody>")}var n=$("<tbody></tbody>").appendTo(m);for(var a in b){var d=b[a];var c=addresses[d];if(!c){continue}var l="";if(c.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(c.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var f="";var g=c.addr;if(c.label!=null){g=c.label;f='<span class="hidden-phone"> - '+c.addr+"</span>"}var j='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+c.addr+'" onclick="showAddressModal(\''+c.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+c.addr+'" target="new">'+g+"</a>"+f+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(c.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';n.append(j)}m.append(n)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(n){var e=[];var m=n.filteredTaints;for(var d in m){var f=m[d];var c=-1;for(var g in f){var j=f[g];for(var h in e){if(h!=c&&$.inArray(g,e[h])){if(c>=0){var l=e.splice(c,1);var k=e.splice(h,1);e.push(l.concat(k))}else{e[h].push(d)}c=h}if(c>=0){break}}}if(c==-1){e.push([d])}}buildTable(e);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};