function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(k){var m=$("#active-addresses");var g=m.find("table");g.find("tbody").remove();for(var j in k){var b=k[j];if(j==0){g.append('<tbody><tr><th colspan="2">Group #'+j+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{g.append('<tbody><tr><th colspan="4">Group #'+j+"</th></tr></tbody>")}var d=$("<tbody></tbody>").appendTo(g);for(var a in b){var c=b[a];var h=addresses[c];if(!h){continue}var f="";if(h.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(h.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var l="";var e=h.addr;if(h.label!=null){e=h.label;l='<span class="hidden-phone"> - '+h.addr+"</span>"}var n='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+h.addr+'" onclick="showAddressModal(\''+h.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+h.addr+'" target="new">'+e+"</a>"+l+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(h.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';d.append(n)}g.append(d)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(k){var j=[];var e=k.filteredTaints;for(var d in e){var g=e[d];var c=-1;for(var n in g){var m=g[n];for(var h in j){if(h!=c&&$.inArray(n,j[h])){if(c>=0){var f=j.splice(c,1);var l=j.splice(h,1);j.push(f.concat(l))}else{j[h].push(d)}c=h}if(c>=0){break}}}if(c==-1){j.push([d])}}buildTable(j);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};