function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(a){var l=$("#active-addresses");var k=l.find("table");k.find("tbody").remove();for(var b in a){var m=a[b];if(b==0){k.append('<tbody><tr><th colspan="2">Group #'+b+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{k.append('<tbody><tr><th colspan="4">Group #'+b+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(k);for(var j in m){var n=m[j];var f=addresses[n];if(!f){continue}var e="";if(f.tag==1){e=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(f.priv==null){e=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var d="";var c=f.addr;if(f.label!=null){c=f.label;d='<span class="hidden-phone"> - '+f.addr+"</span>"}var g='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+f.addr+'" onclick="showAddressModal(\''+f.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+f.addr+'" target="new">'+c+"</a>"+d+" "+e+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(f.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(g)}k.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(j){var c=[];var k=j.filteredTaints;for(var n in k){var g=k[n];var d=-1;for(var h in g){var m=g[h];for(var e in c){if(e!=d&&$.inArray(h,c[e])){if(d>=0){var f=c.splice(d,1);var l=c.splice(e,1);c.push(f.concat(l))}else{c[e].push(n)}d=e}if(d>=0){break}}}if(d==-1){c.push([n])}}buildTable(c);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(c)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};