function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(a){var d=$("#active-addresses");var h=d.find("table");h.find("tbody").remove();for(var f in a){var c=a[f];if(f==0){h.append('<tbody><tr><th colspan="2">Group #'+f+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+f+"</th></tr></tbody>")}var j=$("<tbody></tbody>").appendTo(h);for(var l in c){var n=c[l];var m=addresses[n];if(!m){continue}var g="";if(m.tag==1){g=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(m.priv==null){g=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var e=m.addr;if(m.label!=null){e=m.label;b='<span class="hidden-phone"> - '+m.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+m.addr+'" onclick="showAddressModal(\''+m.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+m.addr+'" target="new">'+e+"</a>"+b+" "+g+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(m.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';j.append(k)}h.append(j)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(m){var d=[];var f=m.filteredTaints;for(var n in f){var e=f[n];var k=-1;for(var h in e){var c=e[h];for(var g in d){if(g!=k&&$.inArray(h,d[g])){if(k>=0){var j=d.splice(k,1);var l=d.splice(g,1);d.push(j.concat(l))}else{d[g].push(n)}k=g}if(k>=0){break}}}if(k==-1){d.push([n])}}buildTable(d);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(d)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};