function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(j){var d=$("#active-addresses");var c=d.find("table");c.find("tbody").remove();for(var n in j){var b=j[n];if(n==0){c.append('<tbody><tr><th colspan="2">Group #'+n+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{c.append('<tbody><tr><th colspan="4">Group #'+n+"</th></tr></tbody>")}var h=$("<tbody></tbody>").appendTo(c);for(var a in b){var l=b[a];var g=addresses[l];if(!g){continue}var f="";if(g.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(g.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var k="";var m=g.addr;if(g.label!=null){m=g.label;k='<span class="hidden-phone"> - '+g.addr+"</span>"}var e='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+g.addr+'" onclick="showAddressModal(\''+g.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+g.addr+'" target="new">'+m+"</a>"+k+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(g.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';h.append(e)}c.append(h)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var j=[];var h=l.filteredTaints;for(var k in h){var f=h[k];var d=-1;for(var e in f){var g=f[e];for(var m in j){if(m!=d&&$.inArray(e,j[m])){if(d>=0){var n=j.splice(d,1);var c=j.splice(m,1);j.push(n.concat(c))}else{j[m].push(k)}d=m}if(d>=0){break}}}if(d==-1){j.push([k])}}buildTable(j);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};