function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(l){var f=$("#active-addresses");var k=f.find("table");k.find("tbody").remove();for(var c in l){var a=l[c];if(c==0){k.append('<tbody><tr><th colspan="2">Group #'+c+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{k.append('<tbody><tr><th colspan="4">Group #'+c+"</th></tr></tbody>")}var g=$("<tbody></tbody>").appendTo(k);for(var n in a){var h=a[n];var m=addresses[h];if(!m){continue}var d="";if(m.tag==1){d=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(m.priv==null){d=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var j=m.addr;if(m.label!=null){j=m.label;b='<span class="hidden-phone"> - '+m.addr+"</span>"}var e='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+m.addr+'" onclick="showAddressModal(\''+m.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+m.addr+'" target="new">'+j+"</a>"+b+" "+d+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(m.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';g.append(e)}k.append(g)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(c){var m=[];var k=c.filteredTaints;for(var h in k){var l=k[h];var n=-1;for(var e in l){var g=l[e];for(var f in m){if(f!=n&&$.inArray(e,m[f])){if(n>=0){var j=m.splice(n,1);var d=m.splice(f,1);m.push(j.concat(d))}else{m[f].push(h)}n=f}if(n>=0){break}}}if(n==-1){m.push([h])}}buildTable(m);apiGetBalances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(m)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};