function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(e){var c=$("#active-addresses");var h=c.find("table");h.find("tbody").remove();for(var n in e){var l=e[n];if(n==0){h.append('<tbody><tr><th colspan="2">Group #'+n+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+n+"</th></tr></tbody>")}var j=$("<tbody></tbody>").appendTo(h);for(var d in l){var g=l[d];var f=addresses[g];if(!f){continue}var m="";if(f.tag==1){m=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(f.priv==null){m=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var b="";var a=f.addr;if(f.label!=null){a=f.label;b='<span class="hidden-phone"> - '+f.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+f.addr+'" onclick="showAddressModal(\''+f.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+f.addr+'" target="new">'+a+"</a>"+b+" "+m+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(f.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';j.append(k)}h.append(j)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(j){var e=[];var g=j.filteredTaints;for(var f in g){var d=g[f];var c=-1;for(var l in d){var h=d[l];for(var n in e){if(n!=c&&$.inArray(l,e[n])){if(c>=0){var k=e.splice(c,1);var m=e.splice(n,1);e.push(k.concat(m))}else{e[n].push(f)}c=n}if(c>=0){break}}}if(c==-1){e.push([f])}}buildTable(e);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(e)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};