function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(n){var d=$("#active-addresses");var c=d.find("table");c.find("tbody").remove();for(var g in n){var h=n[g];if(g==0){c.append('<tbody><tr><th colspan="2">Group #'+g+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{c.append('<tbody><tr><th colspan="4">Group #'+g+"</th></tr></tbody>")}var k=$("<tbody></tbody>").appendTo(c);for(var m in h){var f=h[m];var b=addresses[f];if(!b){continue}var j="";if(b.tag==1){j=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(b.priv==null){j=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var e="";var a=b.addr;if(b.label!=null){a=b.label;e='<span class="hidden-phone"> - '+b.addr+"</span>"}var l='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+b.addr+'" onclick="showAddressModal(\''+b.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+b.addr+'" target="new">'+a+"</a>"+e+" "+j+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(b.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';k.append(l)}c.append(k)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(f){var n=[];var k=f.filteredTaints;for(var h in k){var m=k[h];var e=-1;for(var c in m){var d=m[c];for(var j in n){if(j!=e&&$.inArray(c,n[j])){if(e>=0){var g=n.splice(e,1);var l=n.splice(j,1);n.push(g.concat(l))}else{n[j].push(h)}e=j}if(e>=0){break}}}if(e==-1){n.push([h])}}buildTable(n);BlockchainAPI.get_balances(getAllAddresses(),function(a){for(var b in a){addresses[b].balance=a[b].final_balance}buildTable(n)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};