function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(c){var m=$("#active-addresses");var h=m.find("table");h.find("tbody").remove();for(var a in c){var b=c[a];if(a==0){h.append('<tbody><tr><th colspan="2">Group #'+a+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{h.append('<tbody><tr><th colspan="4">Group #'+a+"</th></tr></tbody>")}var g=$("<tbody></tbody>").appendTo(h);for(var e in b){var n=b[e];var k=addresses[n];if(!k){continue}var j="";if(k.tag==1){j=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(k.priv==null){j=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var l="";var f=k.addr;if(k.label!=null){f=k.label;l='<span class="hidden-phone"> - '+k.addr+"</span>"}var d='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+k.addr+'" onclick="showAddressModal(\''+k.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+k.addr+'" target="new">'+f+"</a>"+l+" "+j+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(k.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';g.append(d)}h.append(g)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(l){var f=[];var n=l.filteredTaints;for(var m in n){var k=n[m];var g=-1;for(var e in k){var h=k[e];for(var d in f){if(d!=g&&$.inArray(e,f[d])){if(g>=0){var c=f.splice(g,1);var j=f.splice(d,1);f.push(c.concat(j))}else{f[d].push(m)}g=d}if(g>=0){break}}}if(g==-1){f.push([m])}}buildTable(f);BlockchainAPI.get_balances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(f)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};