function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(h){var a=$("#active-addresses");var j=a.find("table");j.find("tbody").remove();for(var d in h){var k=h[d];if(d==0){j.append('<tbody><tr><th colspan="2">Group #'+d+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{j.append('<tbody><tr><th colspan="4">Group #'+d+"</th></tr></tbody>")}var g=$("<tbody></tbody>").appendTo(j);for(var c in k){var m=k[c];var l=addresses[m];if(!l){continue}var f="";if(l.tag==1){f=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){f=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var n="";var e=l.addr;if(l.label!=null){e=l.label;n='<span class="hidden-phone"> - '+l.addr+"</span>"}var b='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+e+"</a>"+n+" "+f+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';g.append(b)}j.append(g)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(f){var g=[];var m=f.filteredTaints;for(var j in m){var n=m[j];var k=-1;for(var l in n){var h=n[l];for(var d in g){if(d!=k&&$.inArray(l,g[d])){if(k>=0){var e=g.splice(k,1);var c=g.splice(d,1);g.push(e.concat(c))}else{g[d].push(j)}k=d}if(k>=0){break}}}if(k==-1){g.push([j])}}buildTable(g);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(g)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};