function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(m){var c=$("#active-addresses");var b=c.find("table");b.find("tbody").remove();for(var n in m){var k=m[n];if(n==0){b.append('<tbody><tr><th colspan="2">Group #'+n+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{b.append('<tbody><tr><th colspan="4">Group #'+n+"</th></tr></tbody>")}var a=$("<tbody></tbody>").appendTo(b);for(var g in k){var d=k[g];var l=addresses[d];if(!l){continue}var j="";if(l.tag==1){j=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(l.priv==null){j=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var h="";var f=l.addr;if(l.label!=null){f=l.label;h='<span class="hidden-phone"> - '+l.addr+"</span>"}var e='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+l.addr+'" onclick="showAddressModal(\''+l.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+l.addr+'" target="new">'+f+"</a>"+h+" "+j+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(l.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';a.append(e)}b.append(a)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(c){var k=[];var f=c.filteredTaints;for(var e in f){var d=f[e];var h=-1;for(var g in d){var j=d[g];for(var n in k){if(n!=h&&$.inArray(g,k[n])){if(h>=0){var m=k.splice(h,1);var l=k.splice(n,1);k.push(m.concat(l))}else{k[n].push(e)}h=n}if(h>=0){break}}}if(h==-1){k.push([e])}}buildTable(k);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(k)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};