function removeGrouping(){$("#active-addresses").trigger("show")}function buildTable(j){var a=$("#active-addresses");var b=a.find("table");b.find("tbody").remove();for(var e in j){var n=j[e];if(e==0){b.append('<tbody><tr><th colspan="2">Group #'+e+'</th><th colspan="2"><a onclick="removeGrouping();">Hide Grouping</a></th></tr></tbody>')}else{b.append('<tbody><tr><th colspan="4">Group #'+e+"</th></tr></tbody>")}var c=$("<tbody></tbody>").appendTo(b);for(var f in n){var d=n[f];var m=addresses[d];if(!m){continue}var l="";if(m.tag==1){l=' <font color="red" title="Not Synced">(Not Synced)</font>'}else{if(m.priv==null){l=' <font color="red" title="Watch Only">(Watch Only)</font>'}}var g="";var h=m.addr;if(m.label!=null){h=m.label;g='<span class="hidden-phone"> - '+m.addr+"</span>"}var k='<tr style="background-color:#FFFFFF;"><td style="width:20px;background-color:#FFFFFF;"><img id="qr'+m.addr+'" onclick="showAddressModal(\''+m.addr+'\')" src="'+resource+'info.png" /></td><td style="background-color:#FFFFFF;"><div class="short-addr"><a href="'+root+"address/"+m.addr+'" target="new">'+h+"</a>"+g+" "+l+'<div></td><td style="background-color:#FFFFFF;" colspan="2"><span style="color:green">'+formatBTC(m.balance)+'<span class="hidden-phone"> BTC</span></span></td></tr>';c.append(k)}b.append(c)}}function loadTaintData(){setLoadingText("Loading Taint Data");$.get(root+"taint/"+getAllAddresses().join("|")+"?format=json").success(function(g){var j=[];var m=g.filteredTaints;for(var c in m){var l=m[c];var h=-1;for(var n in l){var f=l[n];for(var e in j){if(e!=h&&$.inArray(n,j[e])){if(h>=0){var d=j.splice(h,1);var k=j.splice(e,1);j.push(d.concat(k))}else{j[e].push(c)}h=e}if(h>=0){break}}}if(h==-1){j.push([c])}}buildTable(j);apiGetBalances(getAllAddresses(),function(b){for(var a in b){addresses[a].balance=b[a].final_balance}buildTable(j)},function(a){makeNotice("error","misc-error",a)})}).error(function(){makeNotice("error","misc-error","Error Downloading Taint Data")})};